!function(e){"use strict";
  e('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function(){
    if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){
      var a=e(this.hash);
      if((a=a.length?a:e("[name="+this.hash.slice(1)+"]")).length)
        return e("html, body").animate({scrollTop:a.offset().top-54},1e3,"easeInOutExpo"),!1
    }
  });
  
  e(".js-scroll-trigger").click(function(){
    e(".navbar-collapse").collapse("hide")
  });
  
  e("body").scrollspy({target:"#mainNav",offset:56});
  
  function a(){
    // Use window.pageYOffset for more reliable scroll detection
    if(window.pageYOffset > 100 || document.documentElement.scrollTop > 100){
      e("#mainNav").addClass("navbar-shrink");
    } else {
      e("#mainNav").removeClass("navbar-shrink");
    }
  }
  
  // Run on load
  a();
  
  // Run on scroll
  e(window).scroll(a);
  
  // Also run after a short delay to catch any layout shifts
  setTimeout(a, 100);
}(jQuery);
